mapper.select:
select d.id ,d.name ,s.name as sortName,d.address,d.createDate,d.price,d.remaining from goods_detail d left join goods_sort s on d.sortId = s.id
and d.sortId=#{sortId,jdbcType=BIGINT}
order by d.id desc

mapper.update:
update goods_detail
set remaining= (remaining - #{buyNum,jdbcType=INTEGER})
where id = #{id,jdbcType=BIGINT}
